        List<GalleryViewModel> holder = new ArrayList<>();

        GalleryViewModel obj0 = new GalleryViewModel();
        obj0.setName("Ower Facalty");
        obj0.setImage("data:image/jpeg;base64,");  // Add image in drawable as ic_water.png
        holder.add(obj0);

        GalleryViewModel obj1 = new GalleryViewModel();

        obj1.setName("park");
        obj1.setImage("https://cdn-icons-png.flaticon.com/512/1531/1531776.png");  // e.g., ic_flat.png
        holder.add(obj1);

